import { ApiProperty } from "@nestjs/swagger";
import { IsBoolean, IsString } from "class-validator";

export class AgvNetworkDto {

    @ApiProperty()
    @IsString()
    id: string;

    @ApiProperty()
    @IsString()
    ip: string;

    @ApiProperty()
    @IsBoolean()
    active: boolean;

    constructor(id: string, ip: string, active: boolean) {
        this.id = id;
        this.ip = ip;
        this.active = active;
    }
}