
import { TYPEALERT } from "../../common/typeAlert";
import { ApiProperty } from "@nestjs/swagger";
import { IsBoolean, IsDate, IsNotEmpty, IsNumber, IsOptional, IsString } from "class-validator";

export class AlertDto {

    @ApiProperty()
    @IsNumber()
    @IsOptional()
    id?:number

    @ApiProperty()
    @IsNotEmpty()
    type: TYPEALERT;

    @ApiProperty()
    @IsString()
    @IsNotEmpty()
    description: string;
    
    @ApiProperty()
    @IsDate()
    date?: Date;

    @ApiProperty()
    @IsBoolean()
    @IsOptional()
    read?: boolean;
}
