import { ApiProperty } from '@nestjs/swagger';

export class Token {
  @ApiProperty()
  accessToken: string;

  @ApiProperty()
  tokenType: string;

  @ApiProperty()
  refreshToken: string;

  constructor(token: string, type: string, refreshToken: string) {
    this.accessToken = token;
    this.tokenType = type;
    this.refreshToken = refreshToken;
  }
}
