import { ApiProperty } from "@nestjs/swagger";
import { IsArray, IsBoolean, IsNumber, IsString } from "class-validator";

export class CreateEditCross {
    @ApiProperty()
    @IsString()
    crossId: string;

    @ApiProperty()
    @IsString()
    description: string;

    @ApiProperty()
    @IsArray()
    blockingTags: number[];

    @ApiProperty()
    @IsArray()
    liberationTags: number[];

    @ApiProperty()
    @IsArray()
    waiting?: string[];

    @ApiProperty()
    @IsBoolean()
    occupied?: boolean;

    @ApiProperty()
    @IsString()
    agvId?: string;
}