import { ApiProperty } from "@nestjs/swagger";
import { IsArray, IsBoolean, IsNumber, IsString } from "class-validator";

export class Cross {
    @ApiProperty()
    @IsString()
    crossId: string;

    @ApiProperty()
    @IsBoolean()
    occupied: boolean;

    @ApiProperty()
    @IsArray()
    waiting: string[];

    @ApiProperty()
    @IsString()
    agvId?: string;

    @ApiProperty()
    @IsString()
    description?: string;

    @ApiProperty()
    @IsArray()
    blockingTags?: number[];

    @ApiProperty()
    @IsArray()
    liberationTags?: number[];

    @ApiProperty()
    @IsString()
    zone?: string;
}