import * as mongoose from 'mongoose';
import { Document } from 'mongoose';

// Create an interface representing a document in MongoDB
export interface ICross extends Document {
  crossId: string;
  occupied: boolean;
  waiting: string[];
  agvId?: string;
  description?: string;
  blockingTags?: number[];
  liberationTags?: number[];
  zone?: string;
}

// Create a Schema corresponding to the document Interface
export const crossSchema = new mongoose.Schema<ICross>(
  {
    crossId: { type: String, required: true, unique: true },
    occupied: {
        type: Boolean,
        required: true,
    },
    waiting: { type: [String] },
    agvId: { type: String },
    description: { type: String},
    blockingTags: [{ type: Number}],
    liberationTags: [{type: Number}],
    zone: { type: String}
  },
);


