import * as mongoose from 'mongoose';
import { Document } from 'mongoose';

export interface IPermission extends Document {
  permissionName: string;
}

export const permissionSchema = new mongoose.Schema<IPermission>(
  {
    permissionName: { 
      type: String, 
      required: true, 
      unique: true,
      uppercase: true,
      trim: true,
    },
  },
  {
    timestamps: true,
  },
);
