import { DynamicModule, Module } from '@nestjs/common';
import { NodeOperationsModule } from '../node-operations/node-operations.module';
import { PlcOpcuaConnectionConfig } from './plc-opcua-connection-config';
import { PlcOpcuaConnectionController } from './plc-opcua-connection.controller';
import { PlcOpcuaConnectionService } from './plc-opcua-connection.service';

@Module({
  imports: [NodeOperationsModule],
  controllers: [PlcOpcuaConnectionController]
})
export class PlcOpcuaConnectionModule {
  static register(options: PlcOpcuaConnectionConfig): DynamicModule {
    return {
      module: PlcOpcuaConnectionModule,
      providers: [
        {
          provide: 'CONFIG_OPTIONS',
          useValue: options,
        },
        PlcOpcuaConnectionService,
      ],
      exports: [PlcOpcuaConnectionService]
    };
  }
}
