import { ApiProperty } from "@nestjs/swagger";
import { IsString } from "class-validator";
import { STATUS } from "../../common/status";

export class StationDto {

  @ApiProperty()
  @IsString()
  name: string;
  
  @ApiProperty()
  @IsString()
  coords: string;
  
  @ApiProperty()
  @IsString()
  status: STATUS;
}
